sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ..
var incidentSysId = inputs.record; // Replace with the actual incident sys_id
var journalValues = []; // Array to store journal entry values

var journalGR = new GlideRecord('sys_journal_field');
journalGR.addQuery('element_id', incidentSysId);
journalGR.orderBy('sys_created_on'); // Order by ascending (oldest to newest)
journalGR.query();

while (journalGR.next()) {
    journalValues.push(journalGR.getValue('value')); // Store each value in the array
}

// Log the array to check the stored values in ascending order
gs.info('Journal Entries (Oldest to Newest): ' + JSON.stringify(journalValues));
//outputs.journal = JSON.stringify(journalValues);
var journalString = journalValues.join('\n');
//outputs.journal = journalString;

function removeSpecialCharacters(response) {
    return response.replace(/\s+/g, ' ')  // Replace multiple spaces with a single space
                  .trim()                  // Remove leading and trailing spaces
                  .replace(/(\r\n|\n|\r)/g, ' '); // Removes anything except letters and spaces
}

// Example response
let response = journalString
// Remove special characters from the response
let cleanedResponse = removeSpecialCharacters(response);

// Log the cleaned response
console.log(cleanedResponse);
outputs.journal = cleanedResponse;


})(inputs, outputs);

